mapper.select:
SELECT u.id, p.product_id, b.buy_id, p.product_name,
p.product_price, p.product_qty
FROM users u, product p, buy b
WHERE u.id = b.users_id AND p.product_id = b.product_id AND b.users_id = #{usersId}
ORDER BY b.buy_id;

mapper.update:
UPDATE buy
SET amount = amount - #{amount} 
WHERE users_id = #{usersId}
AND product_id = #{productId}
